import React from "react";
import { Upload } from "lucide-react";

function App() {
  const handleUploadClick = () => {
    const fileInput = document.getElementById("fileUpload");
    fileInput.click();
  };

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      console.log("File selected:", file.name);
      // TODO: Handle file upload logic here
    }
  };

  return (
    <div className="min-h-screen bg-slate-50 flex items-center justify-center p-4">
      <div className="max-w-xl w-full">
        {/* Main Content Card */}
        <div className="bg-white rounded-2xl shadow-sm p-8 text-center relative overflow-hidden">
          {/* Decorative Background Pattern */}
          <div className="absolute top-0 left-0 w-full h-1 bg-gradient-to-r from-red-500 to-red-600" />

          {/* Title with Japanese Styling */}
          <div className="mb-8">
            <h1 className="text-3xl font-bold text-slate-800 mb-2">
              アニメシーン検索
            </h1>
            <p className="text-slate-600 mb-1">Anime Scene Retrieval Platform</p>
            <p className="text-sm text-slate-600">
              Upload an anime episode to extract and search scenes effortlessly.
            </p>
          </div>

          {/* Upload Section */}
          <div className="border-2 border-dashed border-slate-200 rounded-xl p-8 mb-6 
                          transition-colors hover:border-red-500 cursor-pointer"
               onClick={handleUploadClick}>
            <div className="flex flex-col items-center">
              <div className="w-16 h-16 bg-red-50 rounded-full flex items-center justify-center mb-4">
                <Upload className="w-8 h-8 text-red-500" />
              </div>
              <p className="text-slate-600">ビデオをアップロード</p>
              <p className="text-sm text-slate-500">Upload Video</p>
            </div>
          </div>

          {/* Hidden File Input */}
          <input
            type="file"
            id="fileUpload"
            className="hidden"
            accept="video/*"
            onChange={handleFileChange}
          />

          {/* Upload Button */}
          <button 
            onClick={handleUploadClick}
            className="bg-red-600 text-white px-6 py-3 rounded-lg 
                     hover:bg-red-700 transition-colors
                     shadow-sm hover:shadow-md"
          >
            アップロード
          </button>

          {/* Supported Formats */}
          <div className="mt-4 text-sm text-slate-500">
            <p>対応フォーマット / Supported formats:</p>
            <p>MP4・AVI・MKV</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
